const startyear = Inputs.StartYear || '';
const endyear = Inputs.EndYear || '';

if (!startyear || !endyear) {
    Outputs.Failure();
    return;
}

yearString = startyear + " - " + endyear;
yearValue = startyear + endyear;

const { data, error } = await Noodl.Variables.supabaselocal
    .from('schoolyear')
    .insert([{ desc: yearString, yearvalue: yearValue }]);

if (error) {
    Outputs.error = error.hint;
    Outputs.Failure();
} else {
    Outputs.Success();
}

try {
  // Retrieve records from the 'categories' table
  const {data, error} = await Noodl.Variables.supabaselocal.from('schoolyear')
  .select('id, desc'); // Retrieve records from the 'categories' table

  // Create a JSON array to store the rows
  const jsonArray = [];

  // Iterate through the rows and assign 'desc' to 'Label' and 'id' to 'Value'
  data.forEach(row => {
    const jsonRow = {
      Label: String(row.desc),
      Value: row.id
    };
    jsonArray.push(jsonRow);
  });

  // Output the JSON array
  Outputs.JsonArray = jsonArray;
  Outputs.IsDone = true;
  Outputs.Success();

  
} catch (error) {
  console.log("getSchoolYear: ERROR : ", error);
  Outputs.Error = error;
  Outputs.IsDone = false;
  Outputs.Failure();
  return;
}

const supabase = Noodl.Variables.supabaselocal;

const updateSchoolYear = async (id) => {
    try {
        // Find the record with the provided id
        const { data: record, error } = await supabase
            .from('schoolyear')
            .select('*')
            .eq('id', id);

        if (error) {
            throw new Error(error.message);
        }

        if (record.length === 0) {
            throw new Error('Record not found');
        }

        // Update all records to set 'is_active' to false
        const { error: updateError } = await supabase
            .from('schoolyear')
            .update({ is_active: false })
            .neq('id', id);

        if (updateError) {
            throw new Error(updateError.message);
        }

        // Update the specific record to set 'is_active' to true
        const { error: updateRecordError } = await supabase
            .from('schoolyear')
            .update({ is_active: true })
            .eq('id', id);

        if (updateRecordError) {
            throw new Error(updateRecordError.message);
        }

        Outputs.Success();
    } catch (error) {
        Outputs.error = error.message;
        Outputs.Failure();
    }
};

updateSchoolYear(Inputs.id);

const fetchActiveSchoolYear = async () => {
    const { data, error } = await Noodl.Variables.supabaselocal
        .from('schoolyear')
        .select('*')
        .eq('is_active', true);

    if (error) {
        Outputs.Error = error.message;
        Outputs.Failure();
    } else {
        Outputs.Year = data[0]; // Assuming only one active school year record
        Outputs.Success();
    }
}

fetchActiveSchoolYear();

